import React, { useState } from 'react';
import { Card, Image, Typography, Rate } from 'antd';
import format from 'date-fns/format';

import './MovieCard.css';
import ItemGenres from '../ItemGenres/ItemGenres';

const { Title, Text } = Typography;

const MovieCard = ({ item, genres, dataLocal, onChangeDataLocal }) => {
  const {
    id,
    poster_path: img,
    title,
    overview,
    release_date: date,
    vote_average: rating,
    genre_ids: genresItem,
  } = item;
  let poster;
  if (img) {
    poster = `http://image.tmdb.org/t/p/w500${img}`;
  } else {
    poster = 'error';
  }

  const cutText = (text) => {
    if (text.length) {
      while (text.length >= 125) {
        text = text.split(' ');
        text.pop();
        text = text.join(' ');
      }
    } else {
      return null;
    }
    return text + '...';
  };
  const colorRating = rating <= 3 ? '#E90000' : rating <= 5 ? '#E97E00' : rating <= 7 ? '#E9D100' : '#66E900';

  let formatDate;
  try {
    formatDate = format(Date.parse(date), 'PPP');
  } catch (e) {
    formatDate = 'Will be later';
  }
  const onChangeRating = (stars) => {
    let data = {
      item: item,
      genres: genres,
      starsRating: stars,
    };
    let include = false;
    onChangeDataLocal();
    if (localStorage.getItem('moviesRating')) {
      dataLocal.map((value) => {
        if (value.item.id == id) {
          value.starsRating = stars;
          include = true;
        }
        return value;
      });
      if (!include) {
        localStorage.setItem('moviesRating', JSON.stringify([...dataLocal, data]));
      } else {
        localStorage.setItem('moviesRating', JSON.stringify(dataLocal));
      }
    } else {
      localStorage.setItem('moviesRating', JSON.stringify([data]));
    }
    viewStars();
  };

  const startStars = () => {
    let stars;
    if (dataLocal) {
      dataLocal.forEach(({ item, starsRating }) => {
        if (item.id == id) {
          stars = starsRating;
        }
      });
    }
    return stars ? stars : 0;
  };
  const [star, setStar] = useState(startStars());
  const viewStars = () => {
    if (dataLocal) {
      dataLocal.forEach(({ item, starsRating }) => {
        if (item.id == id) {
          setStar(starsRating);
        }
      });
    }
  };
  return (
    <div>
      <Card hoverable>
        <div className="content">
          <Image
            src={poster}
            className="content__image"
            fallback="data:image/png;base64,"
          />
          <div className="content__wrapper">
            <div className="content__text">
              <div className="content__mobile">
                <div className="mobile__text">
                  <div>
                    <div className="content__title">
                      <Image
                        src={poster}
                        className="image__mobile"
                        fallback="data:image/png;base64,"
                      />
                      <div className="title__text_mobile">
                        <div className="title__rating">
                          <Title level={4} className="title__text">
                            {title}
                          </Title>
                          <div className="rating" style={{ borderColor: colorRating }}>
                            {rating}
                          </div>
                        </div>
                        <div className="content__date">
                          <Text type="secondary">{formatDate}</Text>
                        </div>
                        <div>
                          <ItemGenres genres={genres} genresItem={genresItem} />
                        </div>
                      </div>
                    </div>
                  </div>
                  <div>
                    <Text className="context__descr">{cutText(overview)}</Text>
                  </div>
                </div>
              </div>
              <div className="content__desktop">
                <div>
                  <div className="content__title">
                    <Title level={4} className="title__text">
                      {title}
                    </Title>
                    <div className="rating" style={{ borderColor: colorRating }}>
                      {rating}
                    </div>
                  </div>
                </div>
                <div className="content__date">
                  <Text type="secondary">{formatDate}</Text>
                </div>
                <div>
                  <ItemGenres genres={genres} genresItem={genresItem} />
                </div>
                <div>
                  <Text className="context__descr">{cutText(overview)}</Text>
                </div>
              </div>
            </div>
            <div className="content__rate">
              <Rate
                allowHalf
                defaultValue={star}
                // value={star}
                count={10}
                style={{
                  fontSize: 13,
                  padding: 10,
                }}
                onChange={(clientStars) => {
                  onChangeRating(clientStars);
                }}
              />
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
};

export default MovieCard;
